    private void StartButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_StartButtonActionPerformed
        GuiEvent ge = new GuiEvent(this, 0);
        ge.addParameter(FormatCombo.getSelectedItem());
        ge.addParameter(estimate1.getText());
        ge.addParameter(estimate2.getText());
        ge.addParameter(estimate3.getText());
        ge.addParameter(Agent1Combo.getSelectedItem());
        ge.addParameter(Agent2Combo.getSelectedItem());
        ge.addParameter(Agent3Combo.getSelectedItem());
        myAgent.postGuiEvent(ge);
    }//GEN-LAST:event_StartButtonActionPerformed

    private void FormatComboActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_FormatComboActionPerformed
        String selectedFormat = (String) FormatCombo.getSelectedItem();
        switch (selectedFormat) {
            case "English":
                setAgentCombos(EnglishStrategies);
                break;
            case "Dutch":
                setAgentCombos(DutchStrategies);
                break;
            case "Scottish":
                setAgentCombos(ScottishStrategies);
                break;
            case "First-Price":
                setAgentCombos(FirstPriceStrategies);
                break;
            case "Vickrey":
                setAgentCombos(VickreyStrategies);
                break;
            case "Combo":
                setAgentCombos(ComboStrategies);
                break;
        }
                
        
    }//GEN-LAST:event_FormatComboActionPerformed

